import React,{useRef,useEffect, useState} from 'react';
import classes from './NoticeBoxContainer.module.css';
import NoticeBox from './NoticeBox';
import {getdashboardnotice, marknoticeasread,marknoticeasunread} from '../../../../CommonApps/AllAPICalls';



function containsObject(obj, list) {


    var i;
    for (i = 0; i < list.length; i++) {
        if (list[i].id === obj.id) {
            return true;
        }
    }

    return false;
}






const NoticeBoxContainer =(props)=>{


   //console.log("NoticeBox Container rendering. . .");


   const isMounted = useRef(false);

    useEffect(() => {
    isMounted.current = true;
    props.passMountInfo(true);
    return () => {
            isMounted.current = false
            props.passMountInfo(false);
    }
   }, [props]);





   const markAsReadHandler=(noticeId)=>{
	   marknoticeasread({noticeId,props});	  
   }


  const markAsUnReadHandler=(noticeId)=>{
           marknoticeasunread({noticeId,props});
   }


   const [dashboardNotice, getDashboardNotice]=useState(null);
   let temp_notice=null;
     useEffect(()=>{

        getdashboardnotice({getDashboardNotice});

     },[props.dashboardCourses]);





   //console.log("noticeboxContainer: ", dashboardNotice.results);





return (

<div className={classes.noticeBoxContainer}>







   {  dashboardNotice !==null && dashboardNotice.results !==null && dashboardNotice.results.map((notice, index)=>{

         let noticeId = notice.id;
         return <NoticeBox read={notice.read}
	                   Notice={notice}
	                   key={index}
	                   markAsRead={()=>markAsReadHandler(noticeId)}
	                   markAsUnread={()=>markAsUnReadHandler(noticeId)}
	                   rerender={props.rerender}
	                   userData={props.userData}
		   />


   }) 



   }






</div>

);

}


export default NoticeBoxContainer;
