import React,{useState} from "react";
import classes from "./CreateCourseForm.module.css";
import {AiFillCloseCircle} from "react-icons/ai";
import logoImage from '../../../../../CommonApps/BWlogo.JPG'
import {OptionField,
	  DateField,
	  TimeField,DayField} from '../../../../../CommonApps/FormInputObjects';
import {//getclassobjectbyId, 
	  //getuser,
	  //getsubjectsfromclassandboard,
	  createnewclass,createmulticlass} from '../../../../../CommonApps/AllAPICalls';
//import FadeLoader from "react-spinners/FadeLoader";
//import { css } from "@emotion/react";


/*const override = css`
  display: block;
